import Foundation
import SwiftUI

struct Verdana {
    static let regular16 = FontStyle(font: .custom("Verdana", size: 16), foregroundColor: .primary)
    static let regular18 = FontStyle(font: .custom("Verdana", size: 18), foregroundColor: .primary)
    static let bold20 = FontStyle(font: .custom("Verdana-Bold", size: 20), foregroundColor: .primary)
    static let bold28 = FontStyle(font: .custom("Verdana-Bold", size: 28), foregroundColor: .primary)
    static let boldItalic16 = FontStyle(font: .custom("Verdana-BoldItalic", size: 12), foregroundColor: .primary)
    static let boldItalic18 = FontStyle(font: .custom("Verdana-BoldItalic", size: 14), foregroundColor: .primary)
    static let italic12 = FontStyle(font: .custom("Verdana-Italic", size: 12), foregroundColor: .primary)
    static let italic14 = FontStyle(font: .custom("Verdana-Italic", size: 14), foregroundColor: .primary)
}
